import { Box, Button, Modal, TextField } from "@mui/material";
import { useState } from "react";

const QuizModal = ({ open, quiz, onClose }) => {
  const [quizFormData, setQuizFormData] = useState(quiz);
  const handleClose = () => {
    onClose();
  };

  const handleClickAdd = () => {
    console.log(quizFormData);
  };

  const handleChangeValue = (key, value) => {
    console.log({ key, value });
    const quiz = JSON.parse(JSON.stringify(quizFormData));
    quiz[key] = value;
    setQuizFormData(quiz);
  };

  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
    borderRadius: 4,
    display: "flex",
    flexDirection: "column",
    gap: 2,
  };

  return (
    <Box>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box className="quizForm" sx={style}>
          <Box>Add Quiz</Box>
          <TextField
            variant="standard"
            required
            id="outlined-required-small"
            label="Title"
            value={quiz.title}
            onChange={(event) => handleChangeValue("title", event.target.value)}
          />
          <TextField
            variant="standard"
            required
            id="outlined-required-small"
            label="Passing Score"
            value={quiz.passingScore}
            onChange={(event) =>
              handleChangeValue("passingScore", event.target)
            }
          />
          <TextField
            id="filled-multiline-static-small"
            label="Description"
            multiline
            rows={4}
            variant="filled"
            value={quiz.description}
            onChange={(event) =>
              handleChangeValue("Description", event.target.value)
            }
          />
          <Box className="quizFormButton">
            <Button variant="contained" onClick={() => handleClickAdd()}>
              Add
            </Button>
            <Button variant="contained" onClick={() => handleClose()}>
              Close
            </Button>
          </Box>
        </Box>
      </Modal>
    </Box>
  );
};
export default QuizModal;
